    private void factorizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_factorizarActionPerformed
        long numero=Long.parseLong(numeroText.getText());
        if(control(numero)==true){
          resultadoText.setText(String.valueOf(factorizar(numero)));
        }
        else{
          resultadoText.setText("VALOR NO VÁLIDO");
        }// TODO add your handling code here:
    }//GEN-LAST:event_factorizarActionPerformed

