    public long factorizar(long factorial){
        for(long i=factorial-1;i>0;i--){
            factorial=factorial*i;
        }
        return factorial;
    }
    public boolean control(long n){
        boolean x;
        if (n>=0){
          x=true;
        }
        else{
          x=false; 
        }
        return x;
    }
    //Función recursiva
     public double fact (int numero) {
      if (numero==0)
        return 1;
      else
        return numero * fact(numero-1);
    }

